import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {jwt_decode} from "jwt-decode"; // ✅ decode JWT to get role

export default function LoginPage() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const res = await axios.post("http://localhost:8000/api/token/", formData);
      localStorage.setItem("access", res.data.access);
      localStorage.setItem("refresh", res.data.refresh);

      // ✅ Decode the token to get the role
      const decoded = jwt_decode(res.data.access);
      const role = decoded.role;

      // ✅ Redirect based on user role
      if (role === "admin") {
        navigate("/dashboard/admin");
      } else if (role === "agent") {
        navigate("/dashboard/agent");
      } else {
        navigate("/dashboard/taxpayer");
      }
    } catch (err) {
      setError("❌ Invalid credentials. Please try again.");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 to-indigo-800 flex items-center justify-center px-4">
      <div className="bg-white rounded-xl shadow-lg max-w-md w-full p-8">
        <h2 className="text-2xl font-bold text-center text-indigo-800 mb-6">
          🔐 Login to Taxika
        </h2>
        {error && (
          <div className="bg-red-100 text-red-600 text-sm p-2 rounded mb-4 text-center">
            {error}
          </div>
        )}
        <form onSubmit={handleLogin} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">👤 Username</label>
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              className="mt-1 w-full px-4 py-2 border rounded-md shadow-sm focus:ring focus:ring-indigo-300"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">🔒 Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="mt-1 w-full px-4 py-2 border rounded-md shadow-sm focus:ring focus:ring-indigo-300"
              required
            />
          </div>
          <button
            type="submit"
            className="w-full bg-indigo-700 hover:bg-indigo-800 text-white py-2 rounded-md font-semibold transition"
          >
            🚀 Login
          </button>
        </form>
        <div className="mt-4 text-center text-sm text-gray-600">
          🆕 Don't have an account?{" "}
          <button
            onClick={() => navigate("/register")}
            className="text-indigo-700 hover:underline font-semibold"
          >
            Create one
          </button>
        </div>
      </div>
    </div>
  );
}
