import React from "react";

export default function LandingPage() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-500 to-purple-600 text-white font-sans">
      {/* Header */}
      <header className="flex justify-between items-center p-6 bg-blue-700 shadow-md">
        <h1 className="text-2xl font-bold">KRA iTax Helper</h1>
        <div className="flex items-center space-x-4">
          <a href="#" className="hover:underline">Home</a>
          <a href="#" className="hover:underline">Login</a>
          <button className="bg-blue-500 text-white px-4 py-1 rounded-full text-sm">Kiswahili</button>
        </div>
      </header>

      {/* Hero Section */}
      <section className="text-center py-20 px-6">
        <h2 className="text-4xl md:text-5xl font-bold mb-6">KRA iTax Helper</h2>
        <p className="max-w-2xl mx-auto text-lg text-gray-100 mb-10">
          Simplified tax return preparation tool designed specifically for Kenyan taxpayers. File your annual income tax returns easily, securely, and independently.
        </p>
        <div className="flex justify-center space-x-4">
          <button className="bg-blue-600 hover:bg-blue-700 px-6 py-3 rounded-md font-semibold">Get Started</button>
          <button className="bg-gray-400 hover:bg-gray-500 px-6 py-3 rounded-md font-semibold">Create Account</button>
        </div>
      </section>

      {/* Why Choose Section */}
      <section className="bg-white text-gray-800 py-16 px-6">
        <h3 className="text-3xl font-bold text-center mb-12 text-blue-700">Why Choose KRA iTax Helper?</h3>
        <div className="grid md:grid-cols-3 gap-10 max-w-6xl mx-auto">
          {features.map((item, idx) => (
            <div key={idx} className="text-center">
              <div className="text-5xl mb-4">{item.icon}</div>
              <h4 className="text-xl font-semibold mb-2">{item.title}</h4>
              <p className="text-gray-600">{item.description}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-gray-100 text-gray-600 text-sm py-4 px-6 flex justify-between">
        <span className="font-semibold">YouWare</span>
        <span>¬© {new Date().getFullYear()} Wayne Nganju</span>
      </footer>
    </div>
  );
}

const features = [
  {
    title: "KRA ZIP File Generation",
    description: "Automatically generate KRA-compliant ZIP files ready for direct submission to the iTax portal.",
    icon: "üìÑ",
  },
  {
    title: "Comprehensive Tax Summaries",
    description: "Get detailed tax computation summaries with breakdown of PAYE calculations and relief amounts.",
    icon: "üìä",
  },
  {
    title: "Secure & Compliant",
    description: "Built with Kenya's Data Protection Act compliance and bank-level security for your sensitive information.",
    icon: "üîí",
  },
  {
    title: "Mobile Optimized",
    description: "Works perfectly on mobile devices with offline capabilities for areas with limited internet connectivity.",
    icon: "üì±",
  },
  {
    title: "Made for Kenya",
    description: "Specifically designed for Kenyan tax laws. KRA compliant by design.",
    icon: "üá∞üá™",
  },
  {
    title: "Free for Everyone",
    description: "Completely free tool to democratize tax filing and reduce reliance on cybercaf√©s.",
    icon: "üéÅ",
  },
];
