import React, { useState } from "react";
import axios from "axios";

export default function AdminUserForm({ onSuccess }) {
  const [form, setForm] = useState({
    username: "",
    email: "",
    password: "",
    role: "agent", // default to agent
  });

  const [status, setStatus] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("");

    try {
      await axios.post("http://localhost:8000/api/register/", form, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("access")}`,
        },
      });
      setStatus("‚úÖ Agent/Admin created successfully.");
      setForm({ username: "", email: "", password: "", role: "agent" });
      onSuccess(); // Refresh list
    } catch (err) {
      setStatus("‚ùå Failed to create user. Are you logged in as admin?");
    }
  };

  return (
    <div className="bg-white p-4 rounded-lg shadow mb-6 text-gray-800">
      <h3 className="text-lg font-bold mb-2">‚ûï Register Agent/Admin</h3>
      {status && <p className="text-sm mb-3">{status}</p>}
      <form onSubmit={handleSubmit} className="space-y-2">
        <input
          name="username"
          placeholder="Username"
          value={form.username}
          onChange={handleChange}
          className="w-full px-3 py-1 border rounded"
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          className="w-full px-3 py-1 border rounded"
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={form.password}
          onChange={handleChange}
          className="w-full px-3 py-1 border rounded"
          required
        />
        <select
          name="role"
          value={form.role}
          onChange={handleChange}
          className="w-full px-3 py-1 border rounded"
        >
          <option value="agent">üßë‚Äçüíº Tax Agent</option>
          <option value="admin">üõ†Ô∏è Admin</option>
        </select>
        <button
          type="submit"
          className="w-full bg-indigo-600 text-white py-1 rounded font-semibold"
        >
          ‚úÖ Create User
        </button>
      </form>
    </div>
  );
}
