import React, { useState, useEffect } from "react";
import api from "../api";
import LogoutButton from "../components/LogoutButton";


export default function TaxpayerDashboard() {
  const [taxData, setTaxData] = useState(null);
  const [file, setFile] = useState(null);

  const token = localStorage.getItem("token");

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append("file", file);
    try {
      const res = await api.post("/upload-p9/", formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTaxData(res.data.tax_record);
      alert("P9 uploaded and PAYE calculated!");
    } catch (err) {
      alert("Upload failed.");
    }
  };

  const handleGenerateZip = async () => {
    try {
      const res = await api.get("/generate-zip/", {
        headers: { Authorization: `Bearer ${token}` },
      });
      window.open(res.data.download_url, "_blank");
    } catch (err) {
      alert("ZIP generation failed.");
    }
  };

  return (
    <div>
      <h2>Welcome to your Taxpayer Dashboard ðŸŽ‰</h2>

      <div>
        <h4>ðŸ“¤ Upload P9 Form</h4>
        <input type="file" accept=".csv" onChange={handleFileChange} />
        <button onClick={handleUpload}>Upload</button>
      </div>

      {taxData && (
        <div>
          <h4>ðŸ“Š Tax Summary</h4>
          <p>Gross Income: {taxData.gross_income}</p>
          <p>Taxable Income: {taxData.taxable_income}</p>
          <p>Computed PAYE: {taxData.computed_paye}</p>
        </div>
      )}

      <div>
        <h4>ðŸ“¦ Generate ZIP</h4>
        <button onClick={handleGenerateZip}>Download ZIP</button>
      </div>
    </div>
  );
}
